package com.alura.literalura;import com.alura.literalura.servicios.GutendexApi;import com.alura.literalura.servicios.PostgreSQL;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import java.sql.Date;import java.text.ParseException;import java.text.SimpleDateFormat;import java.time.LocalDate;import java.util.Scanner;@SpringBootApplicationpublic class LiteraluraApplication implements CommandLineRunner {	@Autowired	private GutendexApi gutendexApi;	@Autowired	private PostgreSQL postgreService;	private Scanner teclado;    public static void main(String[] args) {		SpringApplication.run(LiteraluraApplication.class, args);	}	@Override	public void run(String... args) throws ParseException {		byte opcion = 0;		String texto = "";		teclado = new Scanner(System.in);		do {			System.out.println("""										----------------------------------------					1 - Buscar libro por nombre					2 - Listar libros registrados					3 - Listar autores registrados					4 - Listar autores vivos en un año					5 - Listar libros por idioma										0 - Finalizar programa					----------------------------------------										""");			teclado.useDelimiter("[\n\r\t]+");			opcion = teclado.nextByte();			switch (opcion){				case 1:					System.out.println("Ingrese el titulo del libro que desea buscar");					texto = teclado.next();					System.out.println(postgreService.traerLibro(texto));					break;				case 2:					System.out.println("\nLibros registrados");					System.out.println(postgreService.traerLibros());					System.out.println();					break;				case 3:					System.out.println("\nAutores registrados");					System.out.println(postgreService.traerAutores());					System.out.println();					break;				case 4:					System.out.println("Ingrese un año (yyyy)");					int año = teclado.nextInt();					System.out.println("\nAutores en ese año");					System.out.println(postgreService.traerAutores(							LocalDate.of(año,1,1)					));					break;				case 5:					break;			}		}while(opcion != 0);		System.exit(1);	}}